#include <stdint.h>

#include "phy/dp83825i.h"

#define PHY_ID              0x2000A140
#define PHY_ID_MSK          0xfffffff0

#define DP83825I_RESET_HOLD_TIME_MS 10

int dp83825i_init(phy_t *phy, uint8_t addr, phy_read_reg_t read, phy_write_reg_t write, uint32_t init_flags)
{
    phy->addr = addr;
    phy->read = read;
    phy->write = write;

    phy->link_status.is_up = false;

    uint32_t phy_id;
    do
    {
        phy_get_id(phy, &phy_id);
    }
    while (phy_id == PHY_MII_INVALID_PHY_ID);

    if ((phy_id & PHY_ID_MSK) != PHY_ID)
    {
        return -1;
    }

    phy_reset(phy, DP83825I_RESET_HOLD_TIME_MS);

    return 0;
}

int dp83825i_update_link(phy_t *phy)
{
    return phy_update_link(phy);
}

int dp83825i_cfg_link(const phy_t *phy, enum phy_link_speed adv_speeds)
{
    phy_cfg_link(phy, adv_speeds);

    /* Enable and restart auto-negotiation */
    phy_restart_aneg(phy);

    return 0;
}
