#include <stdint.h>
#include <string.h>

#include "phy/rtl8211f.h"

#define PHY_ID              0x001cc910
#define PHY_ID_MSK          0xfffffff0

#define RTL8201F_RESET_HOLD_TIME_MS 10

#define RTL8211F_PHYSR_REG              (0x1A)
#define RTL8211F_PHYSR_LINKSTATUS_MASK  BIT(2)
#define RTL8211F_PHYSR_LINKDUPLEX_MASK  BIT(3)
#define RTL8211F_PHYSR_LINKSPEED_MASK   (BIT(4) | BIT(5))
#define RTL8211F_PHYSR_LINKSPEED_SHIFT  (4U)
#define RTL8211F_PHYSR_LINKSPEED_10M    (0U)
#define RTL8211F_PHYSR_LINKSPEED_100M   (1U)
#define RTL8211F_PHYSR_LINKSPEED_1000M  (2U)

#define RTL8211F_PAGSR_REG              (0x1F)
#define RTL8211F_PHYCR2                 (0x19)

#define RTL8211F_PAGE_MIICR_ADDR        (0xD08)
#define RTL8211F_MIICR1_REG             (0x11)
#define RTL8211F_MIICR2_REG             (0x15)
#define RTL8211F_MIICR1_TXDLY_MASK      BIT(8)
#define RTL8211F_MIICR2_RXDLY_MASK      BIT(3)

static inline void rtl8211f_page_select(const phy_t *phy, uint16_t page)
{
    phy->write(phy->addr, 0, RTL8211F_PAGSR_REG, page);
}

int rtl8211f_init(phy_t *phy, uint8_t addr, phy_read_reg_t read, phy_write_reg_t write, uint32_t init_flags)
{
    phy->addr = addr;
    phy->read = read;
    phy->write = write;

    phy->link_status.is_up = false;

    uint32_t phy_id;
    do
    {
        phy_get_id(phy, &phy_id);
    }
    while (phy_id == PHY_MII_INVALID_PHY_ID);

    if ((phy_id & PHY_ID_MSK) != PHY_ID)
    {
        return -1;
    }

    rtl8211f_page_select(phy, 0xa43);
    uint16_t reg_val = phy->read(phy->addr, 0, RTL8211F_PHYCR2);
    if (init_flags & RTL8211F_CONFIG_ENABLE_CLKOUT)
    {
      /* enable CLKOUT @125MHz */
      reg_val |= 0x1;
    }

    if (init_flags & RTL8211F_CONFIG_DISABLE_EEE)
    {
      reg_val &= (uint16_t)~0x0020;
    }
    phy->write(phy->addr, 0, RTL8211F_PHYCR2, reg_val);

    /* Restore to default page 0 */
    rtl8211f_page_select(phy, 0);

    phy_reset(phy, RTL8201F_RESET_HOLD_TIME_MS);

    /* Set RGMII Tx/Rx Delay. */
    rtl8211f_page_select(phy, RTL8211F_PAGE_MIICR_ADDR);

    reg_val = phy->read(phy->addr, 0, RTL8211F_MIICR1_REG);

    if (init_flags & (RTL8211F_CONFIG_INTERFACE_RGMII_ID |
                      RTL8211F_CONFIG_INTERFACE_RGMII_ID_TXID))
    {
        reg_val |= RTL8211F_MIICR1_TXDLY_MASK;
    }
    else
    {
        reg_val &= (uint16_t)~RTL8211F_MIICR1_TXDLY_MASK;
    }
    phy->write(phy->addr, 0, RTL8211F_MIICR1_REG, reg_val);

    reg_val = phy->read(phy->addr, 0, RTL8211F_MIICR2_REG);

    if (init_flags & (RTL8211F_CONFIG_INTERFACE_RGMII_ID |
                      RTL8211F_CONFIG_INTERFACE_RGMII_ID_RXID))
    {
        reg_val |= RTL8211F_MIICR2_RXDLY_MASK;
    }
    else
    {
        reg_val &= (uint16_t)~RTL8211F_MIICR2_RXDLY_MASK;
    }
    phy->write(phy->addr, 0, RTL8211F_MIICR2_REG, reg_val);

    /* Restore to default page 0 */
    rtl8211f_page_select(phy, 0);

    return 0;
}

int rtl8211f_update_link(phy_t *phy)
{
    if (phy->link_status.is_up || phy_aneg_is_complete(phy))
    {
        phy_link_state_t new_state = {};

        /* Read PHY specific status register */
        phy->write(phy->addr, 0, RTL8211F_PAGSR_REG, 0xa43);
        uint16_t physr_reg = phy->read(phy->addr, 0, RTL8211F_PHYSR_REG);
        phy->write(phy->addr, 0, RTL8211F_PAGSR_REG, 0);

        new_state.is_up = (physr_reg & RTL8211F_PHYSR_LINKSTATUS_MASK) != 0;

        if (new_state.is_up == true)
        {
            bool full_duplex = (physr_reg & RTL8211F_PHYSR_LINKDUPLEX_MASK) != 0;
            uint32_t speed = ((physr_reg & RTL8211F_PHYSR_LINKSPEED_MASK) >> RTL8211F_PHYSR_LINKSPEED_SHIFT);

            switch (speed)
            {
                case RTL8211F_PHYSR_LINKSPEED_100M:
                    new_state.speed = full_duplex ? LINK_FULL_100BASE_T : LINK_HALF_100BASE_T;
                    break;
                case RTL8211F_PHYSR_LINKSPEED_1000M:
                    new_state.speed = full_duplex ? LINK_FULL_1000BASE_T : LINK_HALF_1000BASE_T;
                    break;
                case RTL8211F_PHYSR_LINKSPEED_10M:
                default:
                    new_state.speed = full_duplex ? LINK_FULL_10BASE_T : LINK_HALF_10BASE_T;
                    break;
            }
        }

        if (memcmp(&new_state, &phy->link_status, sizeof(phy_link_state_t)) != 0)
        {
            memcpy(&phy->link_status, &new_state, sizeof(phy_link_state_t));
            return 1;
        }
    }
    
    return 0;
}

int rtl8211f_cfg_link(const phy_t *phy, enum phy_link_speed speeds)
{
    phy_cfg_link(phy, speeds);

    /* Enable and restart auto-negotiation */
    phy_restart_aneg(phy);

    return 0;
}

