#include <stdint.h>
#include <string.h>

#include "phy/rtl8211fsi.h"

#define PHY_ID              0x001cc910
#define PHY_ID_MSK          0xfffffff0

#define RTL8211FSI_RESET_HOLD_TIME_MS 10

#define RTL8211FSI_PAGSR_REG                (0x1F)

#define RTL8211FSI_PAGE_PHYSR_ADDR          (0x0a43)
#define RTL8211FSI_PHYSR_REG                (0x1A)

#define RTL8211FSI_PHYSR_LINKSTATUS_MASK    BIT(2)
#define RTL8211FSI_PHYSR_LINKDUPLEX_MASK    BIT(3)
#define RTL8211FSI_PHYSR_LINKSPEED_MASK     (BIT(4) | BIT(5))
#define RTL8211FSI_PHYSR_LINKSPEED_SHIFT    (4U)
#define RTL8211FSI_PHYSR_LINKSPEED_10M      (0U)
#define RTL8211FSI_PHYSR_LINKSPEED_100M     (1U)
#define RTL8211FSI_PHYSR_LINKSPEED_1000M    (2U)

#define RTL8211FSI_PAGE_PHYCR2_ADDR         (0x0a43)
#define RTL8211FSI_PHYCR2_REG               (0x19)

#define RTL8211FSI_PAGE_LCR_ADDR            (0x0D04)
#define RTL8211FSI_LCR_REG                  (0x10)

/* register in page 0xDC8 */
#define RTL8211FSI_PAGE_SERDES_ANSCR_ADDR   (0x0DC8)
#define RTL8211FSI_SERDES_ANSCR_REG         (0x14)

static inline void rtl8211fsi_page_select(const phy_t *phy, uint16_t page)
{
    phy->write(phy->addr, 0, RTL8211FSI_PAGSR_REG, page);
}

int rtl8211fsi_init(phy_t *phy, uint8_t addr, phy_read_reg_t read, phy_write_reg_t write, uint32_t init_flags)
{
    phy->addr = addr;
    phy->read = read;
    phy->write = write;

    phy->link_status.is_up = false;


    uint32_t phy_id;
    do
    {
        phy_get_id(phy, &phy_id);
    }
    while (phy_id == PHY_MII_INVALID_PHY_ID);

    if ((phy_id & PHY_ID_MSK) != PHY_ID)
    {
        return -1;
    }

    /* The bootstrapping by default sets the Operation Mode Configuration to 0x0 : UTP <-> RGMII */
    /* For the TC45 TRB we need to change the Operation Mode Configuration to 0x3 : UTP <-> SGMII*/
    rtl8211fsi_page_select(phy, 0xd40);
    uint16_t reg_val = phy->read(phy->addr, 0, 0x10);
    phy->write(phy->addr, 0, 0x10, (reg_val & ~0x7) | 0x3);

    rtl8211fsi_page_select(phy, RTL8211FSI_PAGE_PHYCR2_ADDR);
    reg_val = phy->read(phy->addr, 0, RTL8211FSI_PHYCR2_REG);
    if (init_flags & RTL8211FSI_CONFIG_ENABLE_CLKOUT)
    {
        /* enable CLKOUT @125MHz */
        reg_val |= 0x1;
    }

    if (init_flags & RTL8211FSI_CONFIG_DISABLE_EEE)
    {
        reg_val &= (uint16_t)~0x0020;
    }
    phy->write(phy->addr, 0, RTL8211FSI_PHYCR2_REG, reg_val);

    /* A PHY reset should be issued after setting this bits in PHYCR2 */
    phy_reset(phy, RTL8211FSI_RESET_HOLD_TIME_MS);

    /* page 0xD04 for LED Control Register */
    rtl8211fsi_page_select(phy, RTL8211FSI_PAGE_LCR_ADDR);
    /* we use only LED0 and LED1, we need to change configuration LED0 to 100M, LED1 to 1000M, LED2 off */
    phy->write(phy->addr, 0, RTL8211FSI_LCR_REG, 0x0312);

    /* page 0xDC8 for SERDES ANSCR */
    rtl8211fsi_page_select(phy, RTL8211FSI_PAGE_SERDES_ANSCR_ADDR);
    /* Serdes AutoNeg. bypass, Enable Bypass SGMII Nway */
    reg_val = phy->read(phy->addr, 0, RTL8211FSI_SERDES_ANSCR_REG);
    phy->write(phy->addr, 0, RTL8211FSI_SERDES_ANSCR_REG, reg_val | 0x2);

    /* Restore to default page 0 */
    rtl8211fsi_page_select(phy, 0);

    return 0;
}

int rtl8211fsi_update_link(phy_t *phy)
{
    if (phy->link_status.is_up || phy_aneg_is_complete(phy))
    {
        phy_link_state_t new_state = {};

        /* Read PHY specific status register */
        rtl8211fsi_page_select(phy, RTL8211FSI_PAGE_PHYSR_ADDR);
        uint16_t physr_reg = phy->read(phy->addr, 0, RTL8211FSI_PHYSR_REG);
        rtl8211fsi_page_select(phy, 0);

        new_state.is_up = (physr_reg & RTL8211FSI_PHYSR_LINKSTATUS_MASK) != 0;

        if (new_state.is_up == true)
        {
            bool full_duplex = (physr_reg & RTL8211FSI_PHYSR_LINKDUPLEX_MASK) != 0;
            uint32_t speed = ((physr_reg & RTL8211FSI_PHYSR_LINKSPEED_MASK) >> RTL8211FSI_PHYSR_LINKSPEED_SHIFT);

            switch (speed)
            {
                case RTL8211FSI_PHYSR_LINKSPEED_100M:
                    new_state.speed = full_duplex ? LINK_FULL_100BASE_T : LINK_HALF_100BASE_T;
                    break;
                case RTL8211FSI_PHYSR_LINKSPEED_1000M:
                    new_state.speed = full_duplex ? LINK_FULL_1000BASE_T : LINK_HALF_1000BASE_T;
                    break;
                case RTL8211FSI_PHYSR_LINKSPEED_10M:
                default:
                    new_state.speed = full_duplex ? LINK_FULL_10BASE_T : LINK_HALF_10BASE_T;
                    break;
            }
        }

        if (memcmp(&new_state, &phy->link_status, sizeof(phy_link_state_t)) != 0)
        {
            memcpy(&phy->link_status, &new_state, sizeof(phy_link_state_t));
            return 1;
        }
    }

    return 0;
}

int rtl8211fsi_cfg_link(const phy_t *phy, enum phy_link_speed speeds)
{
    phy_cfg_link(phy, speeds);

    /* Enable and restart auto-negotiation */
    phy_restart_aneg(phy);

    return 0;
}


